mapper.select:
select

,

from T_BRAND


    and BRAND_ID=#{brandId,jdbcType=INTEGER}


    and COMPANY_ID=#{companyId,jdbcType=INTEGER}


    and BRAND_NATURE=#{brandNature,jdbcType=BIT}


    and BRAND_NAME like concat('%',#{brandName,jdbcType=VARCHAR},'%')


    and BRAND_NAME_EN=#{brandNameEn,jdbcType=VARCHAR}


    and MANUFACTURER=#{manufacturer,jdbcType=VARCHAR}


    and BRAND_WEBSITE=#{brandWebsite,jdbcType=VARCHAR}


    and owner=#{owner,jdbcType=VARCHAR}


    and LOGO_DOMAIN=#{logoDomain,jdbcType=VARCHAR}


    and LOGO_URI=#{logoUri,jdbcType=VARCHAR}


    and SORT=#{sort,jdbcType=INTEGER}


    and INITIAL_CN=#{initialCn,jdbcType=VARCHAR}


    and INITIAL_EN=#{initialEn,jdbcType=VARCHAR}


    and SOURCE=#{source,jdbcType=BIT}


    and ADD_TIME=#{addTime,jdbcType=BIGINT}


    and CREATOR=#{creator,jdbcType=INTEGER}


    and MOD_TIME=#{modTime,jdbcType=BIGINT}


    and UPDATER=#{updater,jdbcType=INTEGER}


    and IS_DELETE=0


    and COMMENTS=#{comments,jdbcType=VARCHAR}


    and COMPANY_NAME=#{companyName,jdbcType=VARCHAR}



order by sort desc

SELECT BRAND_ID as brandId,BRAND_NAME as brandName FROM T_BRAND WHERE BRAND_ID in(
#{index}
) AND COMPANY_ID = 1 AND IS_DELETE = 0
